def get_data(av):
    url = f'http://api.bilibili.com/archive_stat/stat?aid={av}&type=jsonp'
    # print(url)
    response = requests.get(url=url,headers=dailichi())
    txt = response.json()['data']
    with open('b站_播放_评论_硬币数.txt','a+',encoding='utf-8')as f:
        f.write(
            f'{str(av)},'
            + str(txt['view'])
            + ','
            + str(txt['like'])
            + ','
            + str(txt['reply'])
            + ','
            + str(txt['coin'])
            + ','
            + str(txt['share'])
            + ','
            + str(txt['favorite'])
            + '\n'
        )

def duoxiancheng(Hanshu,List,Time):
   import time
   from concurrent.futures import ThreadPoolExecutor
   pool= ThreadPoolExecutor(max_workers=10)
   for i in List:
       pool.submit(Hanshu,i)
       time.sleep(Time)
   pool.shutdown()
def get_tag(av):
    url = f'http://api.bilibili.com/x/tag/archive/tags?aid={av}&jsonp=jsonp'
    response = requests.get(url=url,headers=dailichi())
    txt = response.json()
    with open('tag.txt','a+',encoding='utf-8')as f:
        f.write(f'{str(av)},')
        for x in txt['data']:
            # print(x['tag_name'])
            f.write(x['tag_name']+' ')
        f.write('\n')
def get_pin(av):
    for i in range(1,2):
        url = f'http://api.bilibili.com/x/v2/reply?jsonp=jsonp&pn={i}&type=1&oid={av}'
        response = requests.get(url=url,headers=dailichi())
        txt = response.json()['data']["replies"]
        for x in txt:
            print(x['content']['message'])
def get_title(av):
    try:
        url = f"https://www.bilibili.com/video/av{av}"
        headers = {
            'Content-Type': "text/plain;charset=UTF-8",
            'Origin': "https://www.bilibili.com",
            'Referer': "https://www.bilibili.com/video/av79345669",
            'User-Agent': "Mozilla/5.0 (Windows NT 10.0; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/74.0.3729.169 Safari/537.36",
            'Accept': "*/*",
            'Cache-Control': "no-cache",
            'Postman-Token': "d281ad34-1aba-4bfe-a1ca-cdcf469279f1,1d0ec2ad-c686-4e41-80e6-d1db42328052",
            'Host': "www.bilibili.com",
            'Accept-Encoding': "gzip, deflate",
            'Connection': "keep-alive",
            'cache-control': "no-cache"
            }
        response = requests.request("GET", url, headers=headers)
        title=re.findall('<title data-vue-meta="true">(.*?)</title>',response.text)[0]
        title = title.replace('_哔哩哔哩 (゜-゜)つロ 干杯~-bilibili','')
        # print(title)
        with open('title.txt','a+',encoding='utf-8')as f:
            f.write(f'{str(av)},{title}' + '\n')
    except:
        with open('title.txt','a+',encoding='utf-8')as f:
            f.write(f'{str(av)},' + '\n')
def main(av):
    get_data(av)
    get_tag(av)
    get_title(av)
if __name__ == "__main__":
    url_list = list(set(url_list))
    # print(len(url_list)) 
    duoxiancheng(main,url_list,3)

